var submitName=$("#submitName");function writeUserData(e,a,s){firebase.database().ref("users").push({userID:s,firstname:e,lastname:a})}function addUser(){var e=firebase.database().ref().child("users"),a=$("#fname").val(),s=$("#lname").val();e.once("value",function(n){if(null===n.val()){console.log(1),writeUserData(a,s,1)}else e.limitToLast(1).once("child_added",function(e){var n=e.child("userID").val()+1;console.log(n),writeUserData(a,s,n)})}),alertify.alert("Success","Your information has been submitted",function(){location.reload()})}function checkIfUserExists(e){firebase.database().ref().child("users").child(e).on("value",function(e){console.log(e.val()),exists=null!==e.val()}),console.log(exists)}$(document).ready(function(){submitName.click(function(e){e.preventDefault(),addUser()})});